# Functions
def start_quiz():
    global user_name
    clear_frame()
    
    # Ζητάμε όνομα από τον χρήστη
    name_label = tk.Label(root, text="Enter your name:", font=("Arial", 14), bg="#EAF6F6")
    name_label.pack(pady=10)
    
    name_entry = tk.Entry(root, font=("Arial", 14))
    name_entry.pack(pady=10)
    
    start_button = tk.Button(root, text="Start Quiz", font=("Arial", 14),
                             command=lambda: save_user_name(name_entry.get()))
    start_button.pack(pady=10)

def save_user_name(name):
    global user_name, current_question, score, selectedq
    if name.strip() == "":
        messagebox.showwarning("Attention", "Please enter a name.")
        return
    user_name = name
    #current_question = 0
    score = 0

    selectedq = random.sample(questions, 10)
    current_question = 0
    show_question(current_question)#

def show_question(index):
    clear_frame()
    
    #index = random.randint(0, len(questions) - 1) #########
    question_label = tk.Label(root, text=questions[index]["q"], font=("Arial", 14), bg="#EAF6F6")
    question_label.pack(pady=20) 
    for i, option in enumerate(selectedq[index]["options"]):
        
        button = tk.Button(root, text=option, width=50, command=lambda i=i: check_answer(i))
        button.pack(pady=5)
        
    back_back = tk.Button(root, text="Menu", font=("Arial", 14), command=show_main_menu)
    back_back.pack(pady=20)


def check_answer(selected):
    global score, current_question
    index = selectedq[current_question]["correct"]
    if selected == index:
        score += 1
        messagebox.showinfo("Correct!", "Correct Answer!")
    else:
        correct_ans = selectedq[index]["options"][index]
        messagebox.showinfo("Wrong!", f"Wrong answer! Correct answer: {correct_ans}\nExplanation: {questions[index]['explanation']}")
    
    current_question += 1
    if current_question < len(selectedq):
        show_question(current_question)#
    else:
        show_score()

def show_score():
    clear_frame()
    score_label = tk.Label(root, text=f"{user_name}, your score: {score}/{len(selectedq)}", font=("Arial", 18), bg="#EAF6F6")
    score_label.pack(pady=20)
    save_score_button = tk.Button(root, text="Save Score", command=save_score)
    save_score_button.pack(pady=10)

def save_score():
    with open("scores.txt", "a") as file:
        file.write(f"{user_name}: {score}/{len(selectedq)}\n")
    messagebox.showinfo("Score saved!")
    clear_frame()
    show_main_menu()

def show_scoreboard():
    clear_frame()
    try:
        with open("scores.txt", "r") as file:
            scores = file.readlines()
    except FileNotFoundError:
        scores = ["There are not scores yet."]
    
    score_label = tk.Label(root, text="Score board", font=("Arial", 18), bg="#EAF6F6")
    score_label.pack(pady=20)
    
    for score in scores:
        score_item = tk.Label(root, text=score, bg="#EAF6F6")
        score_item.pack()
    
    # Προσθήκη κουμπιού επιστροφής
    back_button = tk.Button(root, text="Menu", font=("Arial", 14), command=show_main_menu)
    back_button.pack(pady=20)

def show_map_menu():
    clear_frame()

    screen_width = root.winfo_screenwidth()
    screen_height = root.winfo_screenheight()

    # Ρύθμιση παραθύρου να καλύπτει όλη την οθόνη
    root.geometry(f"{screen_width}x{screen_height}")
    # Φόρτωση της εικόνας φόντου του μενού
    executable_dir = os.path.dirname(__file__)
    dd3=os.path.join(executable_dir, 'earth1.jpg')
    background_image_path = dd3  # Διαδρομή προς την εικόνα φόντου
    background_image = Image.open(background_image_path)
    background_image = background_image.resize((screen_width, screen_height), Image.ANTIALIAS)  # Ρύθμιση μεγέθους
    bg_img = ImageTk.PhotoImage(background_image)
    
    # Δημιουργία ενός Label για το φόντο
    bg_label = tk.Label(root, image=bg_img)
    bg_label.image = bg_img  # Κρατήστε μια αναφορά στην εικόνα για να μην απορριφθεί
    bg_label.place(x=0, y=0, relwidth=1, relheight=1)  # Τοποθέτηση του φόντου

    continents = ["Greece"]
    for continent in continents:
        button = tk.Button(root, text=continent, font=("Arial", 5), command=lambda c=continent: show_continent_map(c))
        button.pack(pady=5)
        button.place(x=750, y=200)
    
    # Κουμπί επιστροφής
    back_button = tk.Button(root, text="Menu", font=("Arial", 14), command=show_main_menu)
    back_button.pack(pady=20)

