    title_label = tk.Label(root, text="Volunteering", font=("Arial", 24), bg="#EAF6F6")
    title_label.pack(pady=20)

    # Εμφάνιση των δραστηριοτήτων σε παραλληλόγραμμα
    for activity in volunteer_activities:
        frame = tk.Frame(root, bg="#F2F2F2", padx=10, pady=10)
        frame.pack(pady=10, fill="x", padx=50)

        # Εικόνα στα αριστερά
